import {useState, useEffect} from "react";
import CardList from "./CardList";
import Form from "./Form";

function App() {
  //set the state to store the card data in
  const [cards, setCards] = useState([])
  //make our fetch call to the server we created with our db.json using useEffect
  //and set the data we get back to the cards variable
  useEffect(() => {
    fetch('http://localhost:3001/questions')
    .then(response => response.json())
    .then(data => setCards(data))
  }, [])
  //create handle form function to add new cards to the list
  const handleForm = (newCard) => setCards(currentCardList => {
    return [...currentCardList, newCard]
    }
  )
  //create the handleClick function to shuffle the cards and return them
  return (
    <div className="App">
      <div>
      <CardList cards={ cards } />
      </div>
      <div>
        <Form handleForm={handleForm}/>
      </div>
    </div>
  );
}

export default App;
