import { useState, useEffect } from 'react'

function Form ({ handleForm }) {
    //set state variables for the new questions and answers
    const [question, setQuestion] = useState('')
    const [answer, setAnswer] = useState('')
    //create handle submit function that posts the new card
    const handleSubmit = (e) => {
        //don't forget preventDefault it's a must when dealing with forms
        e.preventDefault()
        //create an object to structure the cards added
        //don't worry about the id it gets handled automagically
        const newCardObject = {
            question : question,
            answer : answer 
        }
        fetch('http://localhost:3001/questions', {
            method: 'POST',
            headers: {
                'Content-Type' : 'application/json'
            },
            body : JSON.stringify(newCardObject)
        })
        .then(response => response.json())
        .then(data => handleForm(data))

    }

    return (
        <div className='form' onSubmit={handleSubmit}>
            <form>
                <input 
                placeholder='Enter question'
                onChange={(e) => setQuestion(e.target.value)} 
                value={question} 
                />
                <input 
                placeholder='Enter answer'
                onChange={(e) => setAnswer(e.target.value)} 
                value={answer}
                />
                <input type='submit' value='add your flashcard to the list' />
            </form>
        </div>
    )
}
export default Form
